"use strict";

var app = angular.module("distantLearning",
[
    "ngRoute",
    "LocalStorageModule",
    "ngResource",
    "ngMaterial",
    "ui.router",
    "ngCookies",
    "ngProgress"
]);
app.config(routes);

function routes($stateProvider, $httpProvider, $urlRouterProvider) {

    $httpProvider.interceptors.push("authInterceptorService");

    var Auth = function ($q, authService) {
        authService.fillAuthData();
        if (authService.authentication.isAuth) {
            return $q.when(authService.authentication);
        } else {
            return $q.reject({authenticated: false});
        }
    };

    $urlRouterProvider.otherwise("/");

    $stateProvider
        .state("main", {
            url: "/",
            templateUrl: "/home/index",
            controller: "mainController",
            abstract: true
        })
        .state("login", {
            url: "/login",
            templateUrl: "../app/account/login.html",
            controller: "loginController"
        })
        .state("signup", {
            url: "/signup",
            templateUrl: "../app/account/signup.html",
            controller: "signupController"
        })
        .state("profile", {
            url: "/profile",
            templateUrl: "../app/profile.html",
            controller: "profileController",
            resolve: {
                auth: Auth
            }
        })
        .state("users", {
            url: "/users",
            templateUrl: "../app/users.html",
            resolve: {
                auth: Auth
            }
        })
        .state("tests", {
            url: "/tests",
            templateUrl: "../app/tests.html",
            resolve: {
                auth: Auth
            }
        })
        .state("journal", {
            url: "/journal",
            templateUrl: "../app/journal.html",
            resolve: {
                auth: Auth
            }
        })
        .state("results", {
            url: "/results",
            templateUrl: "../app/results.html",
            resolve: {
                auth: Auth
            }
        })
        .state("documents", {
            url: "/documents",
            templateUrl: "../app/documents.html",
            controller: "documentController",
            resolve: {
                auth: Auth
            }
        })
        .state("settings", {
            url: "/settings",
            templateUrl: "../app/settings.html",
            resolve: {
                auth: Auth
            }
        })
        .state("help", {
            url: "/help",
            templateUrl: "../app/help.html",
            resolve: {
                auth: Auth
            }
        });
}
app.run(run).config(config);

function run($rootScope, $window, authService) {

    $rootScope.$on("$stateChangeStart",
        function (event, toState) {
            if (toState.external) {
                event.preventDefault();
                $window.open(toState.url, "_self");
            }
        });

    $rootScope.$on("$stateChangeSuccess",
        function (event, toState) {
            if (toState.external) {
                event.preventDefault();
                $window.open(toState.url, "_self");
            }
        });

    authService.fillAuthData();
}

function config($provide, $mdThemingProvider) {
    $provide.decorator("$locale", function ($delegate) {
        return $delegate;
    });

    $mdThemingProvider.theme("default")
        .primaryPalette("light-blue", {
            'default': "800",
            'hue-1': "100",
            'hue-2': "600",
            'hue-3': "A100"
        })
        .accentPalette("pink", {
            'default': "400"
        })
        .warnPalette("red");
}
